const initialState = {
  posts: [],
  loading: false,
}

export default function posts(state = initialState, action) {
  switch (action.type) {
    case 'start':
      return {
        ...state,
        loading: true,
      }
    case 'posts/data':
      return {
        ...state,
        posts: action.payload,
        loading: false,
      }
    default:
      return state
  }
}
export const getPosts = () => {
  return dispatch => {
     dispatch({ type: 'start' })
    fetch('httlocalhost:7777/api/posts/pol')
      .then(res => res.json())
      .then(data => {
        dispatch({ type: 'posts/data', payload: data })
      })
  }
}
